import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Avatar from "../Components/Avatar";
import Start from "../Components/Start"
import Tags from "../Components/Tags"



export default function LocationPage() {
  const [location, setLocation] = useState({
    tags: [],
    equipments: [],
    pictures: [],
    rating: "",
    host: { name: "", picture: "" },
  });
  console.log(location);
  let { id } = useParams();

  useEffect(function () {
    fetch("/locations.json")
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        for (let i = 0; i < data.length; i++) {
          if (data[i].id === id) {
            setLocation(data[i]);
          }
        }
      });
  });

  return (
    <div className="location-info">
      <img src={location.cover} alt="" />
      <div className="location-item">
        <h1>{location.title}</h1>
        <p>{location.location}</p>
        {location.tags.map((tag) => <Tags content={tag} key={tag}/>)}
      </div>
      <div className="profile-prop">
        <Avatar name={location.host.name} picture={location.host.picture} />
        <Start rating={location.rating} key={location.id}/>
        <p className="description">{location.description}</p>
        <h1></h1>
      </div>
    </div>
  );
}
